  /* Select day */
  function setBG(day, calendar) {
    $('.fc-day').removeClass('fc-today');
    $('.fc-day-top').removeClass('fc-today');
    var data = calendar.moment(day).format('YYYY-MM-DD');
    var element = $(".fc-view-container").find("[data-date='" + data + "']");
    $(element).addClass('fc-today');
  }

  /* Center Calendar dotted event */
  function centerDotted() {
    var index = [];
    var posish = [];
    var cont = $('#main-calendar').find('.fc-content-skeleton'); //weeks

    for (var i = cont.length - 1; i >= 0; i--) {
      var tr = $(cont[i]).find('tbody tr');//week

      for (var y = tr.length - 1; y >= 0; y--) {
        var clas = $(tr[y]).find('.fc-event-container');
        if (clas.length > 0) {
          var td = $(tr[y]).find('td');//days

          for (var z = td.length - 1; z >= 0; z--) {
            var dd = $(td[z]).hasClass('fc-event-container');//event-day

            if (dd) {
              index.push((+i+1)+'.'+(+z+1));
              posish.push((+i+1)+'.'+(+z+1)+'.'+(+y+1));
            }
          }
        }
      }
    }

    index.sort();
    posish.sort();

    var current = null;
    var cnt = 0;
    for (var i = 0; i < index.length + 1; i++) {
      if (index[i] != current) {
        if (cnt > 0) {
          //console.log(current + ' comes --> ' + cnt + ' times<br>');
          var row = current.split('.');
          var width;
          for (var n = 1; n < cnt + 1; n++) {
            var count = posish[n-1].split('.');
            //console.log(row, $('.fc-day-grid .fc-week:nth-child(1)'), width, posish);
            if (cnt == 2) {                 
              width = 33 * n;
              $('.fc-day-grid .fc-week:nth-child('+row[0]+') .fc-content-skeleton tbody tr:nth-child('+count[2]+') td:nth-child('+row[1]+') a').css('left', 'Calc('+width +'% - 2px)');
            } else if (cnt == 3) {
              width = (75 / cnt) * n;
              $('.fc-day-grid .fc-week:nth-child('+row[0]+') .fc-content-skeleton tbody tr:nth-child('+count[2]+') td:nth-child('+row[1]+') a').css('left', 'Calc('+width +'% - 2px)');
            } else if (cnt == 1) {
              width = (100 / cnt) * n;
              $('.fc-day-grid .fc-week:nth-child('+row[0]+') .fc-content-skeleton tbody tr:nth-child('+count[2]+') td:nth-child('+row[1]+') a').css('left', 'Calc('+width / 2 +'% - 2px)');
            } else {
              width = (80 / cnt) * n;
              $('.fc-day-grid .fc-week:nth-child('+row[0]+') .fc-content-skeleton tbody tr:nth-child('+count[2]+') td:nth-child('+row[1]+') a').css('left', 'Calc('+width +'% - 2px)');
            }
          }
          for (var n = 1; n < cnt + 1; n++) {
            posish.shift();
          }
        }
        current = index[i];
        cnt = 1;
      } else {
          cnt++;
      }
    }
  }


  $.fullCalendar.DayGrid.prototype.renderSegRow = function renderSegRow(row, rowSegs) {
    var colCnt = this.colCnt;
    var segLevels = this.buildSegLevels(rowSegs); // group into sub-arrays of levels
    var levelCnt = Math.max(1, segLevels.length); // ensure at least one level
    var tbody = $('<tbody/>');
    var segMatrix = []; // lookup for which segments are rendered into which level+col cells
    var cellMatrix = []; // lookup for all <td> elements of the level+col matrix
    var loneCellMatrix = []; // lookup for <td> elements that only take up a single column
    var i, levelSegs;
    var col;
    var tr;
    var j, seg;
    var td;
    //console.log(row, rowSegs,999);

    // populates empty cells from the current column (`col`) to `endCol`
    function emptyCellsUntil(endCol) {
      while (col < endCol) {
        // try to grab a cell from the level above and extend its rowspan. otherwise, create a fresh cell
        td = (loneCellMatrix[i - 1] || [])[col];
        if (td) {
          td.attr(
            'rowspan',
            parseInt(td.attr('rowspan') || 1, 10) + 1
          );
        }
        else {
          td = $('<td/>');
          tr.append(td);
        }
        cellMatrix[i][col] = td;
        loneCellMatrix[i][col] = td;
        col++;
      }
    }

    for (i = 0; i < levelCnt; i++) { // iterate through all levels
      levelSegs = segLevels[i];
      col = 0;
      tr = $('<tr/>');

      segMatrix.push([]);
      cellMatrix.push([]);
      loneCellMatrix.push([]);

      // levelCnt might be 1 even though there are no actual levels. protect against this.
      // this single empty row is useful for styling.
      if (levelSegs) {
        for (j = 0; j < levelSegs.length; j++) { // iterate through segments in level
          seg = levelSegs[j];

          emptyCellsUntil(seg.leftCol);

          // create a container that occupies or more columns. append the event element.
          td = $('<td class="fc-event-container"/>').append(seg.el);
          if (seg.leftCol != seg.rightCol) {
            //td.attr('colspan', seg.rightCol - seg.leftCol + 1);                   
            for (var y = seg.rightCol - seg.leftCol-1; y >= 0; y--) {
              tr.append(td.clone());
            }
          }
          else { // a single-column segment
            loneCellMatrix[i][col] = td;
          }

          while (col <= seg.rightCol) {
            cellMatrix[i][col] = td;
            segMatrix[i][col] = seg;
            col++;
          }

          tr.append(td);
        }
      }

      emptyCellsUntil(colCnt); // finish off the row
      this.bookendCells(tr);
      tbody.append(tr);
    }

    return { // a "rowStruct"
      row: row, // the row number
      tbodyEl: tbody,
      cellMatrix: cellMatrix,
      segMatrix: segMatrix,
      segLevels: segLevels,
      segs: rowSegs
    };
  }


