  function getCoords(elem) { // crossbrowser version
    var box = elem.getBoundingClientRect();

    var body = document.body;
    var docEl = document.documentElement;

    var scrollTop = window.pageYOffset || docEl.scrollTop || body.scrollTop;
    var scrollLeft = window.pageXOffset || docEl.scrollLeft || body.scrollLeft;

    var clientTop = docEl.clientTop || body.clientTop || 0;
    var clientLeft = docEl.clientLeft || body.clientLeft || 0;

    var top  = box.top +  scrollTop - clientTop;
    var left = box.left + scrollLeft - clientLeft;

    return { top: Math.round(top), left: Math.round(left) };
  }

  function dayPosition(argument, res) {
    var pos = getCoords(argument[0]);
    // .outerWidth() takes into account border and padding.
    var width = $(window).outerWidth();

    //show the menu directly over the placeholder
    //var popup = $(argument).parents('.fc-event-container').find('#calendar-popup');

    var left = 40;
    var minusW = pos.left - left + 30;
    var arrowR = 'auto';
    var arrowL =  (30) + (argument.width() / 2) +'px';
    if ( (width - pos.left) < 150) {
      left = 150;
      minusW = pos.left - left + 10;
      arrowL = 'auto';
      arrowR =  (width - pos.left) - (argument.width() / 2) - 15 +'px';
    }

    //$('#calendar-popup .popup-container').text($(this).find('.fc-title').text());
    $('#calendar-popup .arrow').css({
      position: "absolute",
      top: (-10) + "px",
      left: ((pos.left - left) < 0) ? 5 + (argument.width() / 2) + "px" : arrowL,
      right: arrowR
    });


    if (res == 0) {
      $('#calendar-popup').css({
        position: "absolute",
        top: (pos.top + 30) + "px",
        left: ((pos.left - left) < 0) ? 0 + "px" : (pos.left - left) + "px",
        width: (width - minusW) + 'px'
      }).show();

      $('#calendar-popup .close').css('display', 'block');
      
    } else {
      $('#calendar-popup').css({
        position: "absolute",
        top: (pos.top + 30) + "px",
        left: ((pos.left - left) < 0) ? 0 + "px" : (pos.left - left) + "px",
        width: (width - minusW) + 'px'
      });
    }
  }

