(function ($) {
	$(document).ready(function() {
		if(location.search.indexOf('field_training_type_tid') > -1) {
			var $trainings = $('#block-block-7');
			if(!$trainings.length) {
				$trainings = $('#block-block-9');
			}
			$('html, body').animate({scrollTop: $trainings.offset().top + 20}, 500);
		}

		$('.bean-training-block > .content > .field-name-field-description').matchHeight({
			byRow: true,
			property: 'height',
			target: null,
			remove: false
		});

		$('.bean-training-block > .content > .field-name-field-list').matchHeight({
			byRow: true,
			property: 'height',
			target: null,
			remove: false
		});

		$(document).on('click', '.bean-training-block .field-name-field-link a', function() {
			var title = $(this).closest('.entity-bean').find('.bean-title').text().toLowerCase();
			$('.view-filters .radio').each(function() {
				if($(this).find('label').text().toLowerCase() == title) {
					$(this).find('input').attr('checked', true);
					$(this).find('input').trigger('change');
					var trainings = $('#block-block-7');
					if(!trainings.length) {
						trainings = $('#block-block-9');
					}
					$('html, body').animate({scrollTop: trainings.offset().top + 20}, 500);
				}
			});
			return false;
		});

		$('.bean-training-block > .content').append('<div class="field-toggle"></div>');
		$(document).on('click', '.field-toggle', function() {
			$(this).toggleClass('opened');
			$(this).parent().find('.field-name-field-list').slideToggle(300, function() {
				$(this).removeAttr('style').toggleClass('show');
			});
		});

		$(document).on('change', 'select.filter-type', function() {
			$('.views-widget-filter-field_training_type_tid input[value="'+$(this).val()+'"]').attr('checked', true).trigger('change');
		});

		trainingScripts();
		$(document).ajaxComplete(function() {
			trainingScripts();
		});
	});



  function trainingScripts() {
    var filters = $('.view-filters');
    filters.stick_in_parent();

    if(!$('select.filter-type').length) {
      var selectType = '';
      selectType += '<div class="mobile-filter"><select class="filter-type">';
      $('.views-widget-filter-field_training_type_tid .form-item').each(function() {
        var $this = $(this);
        var selected = '';
        if($this.find('input').prop('checked')) {
          selected = ' selected';
        }
        selectType += '<option value="'+$this.find('input').val()+'"'+selected+'>'+$this.find('label').text()+'</option>';
      });
      selectType += '</select></div>';
      filters.append(selectType);
    }

    var select = $('select');
    select.select2();
    select.on('select2:open', function() {
      $('body > .select2-container').width($(this).next().width());
    });

    select.on('select2:close', function(){
      ($('input.select2-search__field').val() == '')?
        $('body.ru input.select2-search__field').val(' ') : false;
        $('body.zone_com input.select2-search__field').val('All products');
    });

    select.on('change', function(){
      if($('.select2-selection__rendered li').length > 1){
        $('body.zone_ru input.select2-search__field').val('');
        $('#edit-field-product-tid-wrapper .form-item input').css({
          'width': '0',
          'margin-left': '0'
        });
      }else{
        $('body.ru input.select2-search__field').val(' ');
        $('body.zone_com input.select2-search__field').val('All products');
        $('#edit-field-product-tid-wrapper .form-item input').css('width','110px');
      }
    });
    setTimeout(function(){
      if($('.select2-selection__rendered li').length < 2) {
        $('body.ru input.select2-search__field').val(' ');
        $('body.zone_com input.select2-search__field').val('All products');
        $('#edit-field-product-tid-wrapper .form-item input').css('width','110px');
      }else{
        $('#edit-field-product-tid-wrapper .form-item input').css({
          'width': '0'
        });
      }
    }, 0)
    $('.views-row .info').matchHeight({
      byRow: true,
      property: 'height',
      target: null,
      remove: false
    });
  }
})(jQuery);
